import React from 'react';
import '../../stylesheets/AppFeed.css';
import '../../stylesheets/fonts.css'
import FeedContent from "./feed-content.component";
import ContentHeader from "../headers/content-header";
import socketIOClient from "socket.io-client";

class AppFeed extends React.Component {

    constructor(props)
    {
        super(props);

        this.state = {
            tweets: [

            ]
        };
    }

    componentDidMount()
    {
        while(this.props.socket)
            this.props.socket.on("tweet", data => {

            this.setState(state =>
            {
                return {
                    tweets: [
                        {
                            text: data.text,
                            user:
                                {
                                    name: data.user.name,
                                    screen_name: data.user.screen_name,
                                    verified: data.user.verified,
                                    profile_image_url: data.user.profile_image_url
                                }
                        },
                        state.tweets[0],
                        state.tweets[1],
                        state.tweets[2],
                        state.tweets[3]
                    ]}
            })

        })

    }

    render(){
        return (

            <div className="app-feed">

                <ContentHeader logoEnabled={true} hdrTitle="Feed"/>

                <div className="feed-content">

                    {
                        this.props.tweetData ?
                        this.props.tweetData.map(tweetObj => (
                            <FeedContent tweetData={tweetObj}/>
                        ))
                            :
                            <>
                                <span>loading...</span>
                            </>
                    }

                </div>

            </div>

        );
    }

}

export default AppFeed;
